import React, { useState } from 'react';

function Task({ task, deleteTask }) {
  const [subtasks, setSubtasks] = useState(task.subtasks);

  const handleSubtaskDone = (subtaskId) => {
    setSubtasks(subtasks.map((subtask) => (subtask.id === subtaskId ? { ...subtask, done: true } : subtask)));
  };

  return (
    <div className="task">
      <div>{task.text}</div>
      {subtasks.map((subtask) => (
        <div key={subtask.id} className={`subtask ${subtask.done ? 'done' : ''}`}>
          {subtask.text}
          {!subtask.done && (
            <button onClick={() => handleSubtaskDone(subtask.id)} className="btn btn-success btn-sm">
              Done
            </button>
          )}
        </div>
      ))}
      <button onClick={() => deleteTask(task.id)} className="btn btn-danger btn-sm">
        X
      </button>
    </div>
  );
}

export default Task;
